mapper.update:

UPDATE total_use_pay
SET use_pay = (SELECT
						use_pay + #{newOrderSheetPrice}
					FROM total_use_pay
					WHERE customer_id = #{customerId})
WHERE customer_id = #{customerId}


UPDATE customer
SET customer_pay = (SELECT
						customer_pay - #{newOrderSheetPrice}
					FROM customer
					WHERE customer_id = #{customerId})
WHERE customer_id = #{customerId}

